import React from "react";
import { useParams } from "react-router";

import Header from "../components/Header";

import star from "../images/star.svg";
import time from "../images/Set_time-01.svg";

const Receipe = () => {
  const { id } = useParams();
  console.log(id);

  return (
    <div>
      <Header />

      <div className="borderMain">
        <div className="flex flex-col gap-3" >
          <div className="flex gap-3 items-end">
            <h1 className="font-bold text-3xl leading-none">Mashala Muglai</h1>
            <div className="bg-green-600 flex items-center justify-center  px-1.5 rounded-sm h-4 ">
              <p className="text-white" style={{ fontSize: "0.5rem" }}>
                4.2
              </p>
              <img src={star} alt="" className="h-2 w-2" />
            </div>
          </div>
          <p className="text-gray-500" >
            Lorem ipsum dolor sit, amet consectetur adipisicing elit. Facere,
            quibusdam ipsum? Commodi obcaecati pariatur alias atque
            exercitationem!
          </p>

            <div className="flex gap-3">
                <img src={time} alt="" />
                <p className="text-sm font-bold" >1 hour</p>
            </div>

        </div>
      </div>
    </div>
  );
};

export default Receipe;
